    private void kaliActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_kaliActionPerformed
        // TODO add your handling code here:
        int bil1 = Integer.parseInt(Bil1.getText());
        int bil2 = Integer.parseInt(Bil2.getText());
        
        int hasilkali = bil1*bil2;
        
        TFHasil.setText(String.valueOf(hasilkali));
    }//GEN-LAST:event_kaliActionPerformed

    private void bagiActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bagiActionPerformed
        // TODO add your handling code here:
        double bil1 = Integer.parseInt(Bil1.getText());
        double bil2 = Integer.parseInt(Bil2.getText());
        
        double hasilbagi = bil1/bil2;
        
        TFHasil.setText(String.valueOf(hasilbagi));
    }//GEN-LAST:event_bagiActionPerformed

    private void plusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_plusActionPerformed
        // TODO add your handling code here:
        int bil1 = Integer.parseInt(Bil1.getText());
        int bil2 = Integer.parseInt(Bil2.getText());
        
        int hasilplus = bil1+bil2;
        
        TFHasil.setText(String.valueOf(hasilplus));
    }//GEN-LAST:event_plusActionPerformed

    private void kurangActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_kurangActionPerformed
        // TODO add your handling code here:
        int bil1 = Integer.parseInt(Bil1.getText());
        int bil2 = Integer.parseInt(Bil2.getText());
        
        int hasilkurang = bil1-bil2;
        
        TFHasil.setText(String.valueOf(hasilkurang));
    }//GEN-LAST:event_kurangActionPerformed

    private void akarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_akarActionPerformed
        // TODO add your handling code here:
        double bil1 = Double.parseDouble(Bil1.getText());
        double bil2 = Integer.parseInt(Bil2.getText());
        
        double hasilakar = Math.sqrt(bil1);
        
        TFHasil.setText(String.valueOf(hasilakar));
    }//GEN-LAST:event_akarActionPerformed

    private void pangkatActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_pangkatActionPerformed
        // TODO add your handling code here:
        double bil1 = Double.parseDouble(Bil1.getText());
        double bil2 = Integer.parseInt(Bil2.getText());
        
        double hasilpangkat = Math.pow(bil1, bil2);
        
        TFHasil.setText(String.valueOf(hasilpangkat));
    }//GEN-LAST:event_pangkatActionPerformed

